import React, { useState, useRef } from 'react';
import './App.css';

function App() {
  const [originalImage, setOriginalImage] = useState(null);
  const [grayscaleImage, setGrayscaleImage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);
  const fileInputRef = useRef(null);

  // Convert image to grayscale using the same algorithm as your Python code
  const convertToGrayscale = (imageData) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Create a temporary image to get dimensions
    const img = new Image();
    img.src = imageData;

    return new Promise((resolve) => {
      img.onload = () => {
        canvas.width = img.width;
        canvas.height = img.height;

        // Draw the original image
        ctx.drawImage(img, 0, 0);

        // Get image data
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
        const data = imageData.data;

        // Convert to grayscale using the same coefficients as your Python code
        // 0.2989 * R + 0.5870 * G + 0.1140 * B
        for (let i = 0; i < data.length; i += 4) {
          const r = data[i];
          const g = data[i + 1];
          const b = data[i + 2];

          const gray = 0.2989 * r + 0.5870 * g + 0.1140 * b;

          data[i] = gray;     // Red
          data[i + 1] = gray; // Green
          data[i + 2] = gray; // Blue
          // Alpha channel remains unchanged
        }

        // Put the modified image data back
        ctx.putImageData(imageData, 0, 0);

        // Convert to blob and create URL
        canvas.toBlob((blob) => {
          const grayscaleUrl = URL.createObjectURL(blob);
          resolve(grayscaleUrl);
        }, 'image/jpeg', 0.9);
      };
    });
  };

  const handleFileUpload = async (file) => {
    if (!file) return;

    // Check if file is an image
    if (!file.type.startsWith('image/')) {
      setError('Please select a valid image file.');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      // Create URL for original image
      const originalUrl = URL.createObjectURL(file);
      setOriginalImage(originalUrl);

      // Convert to grayscale
      const grayscaleUrl = await convertToGrayscale(originalUrl);
      setGrayscaleImage(grayscaleUrl);
    } catch (err) {
      setError('Error processing image. Please try again.');
      console.error('Error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    handleFileUpload(file);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setIsDragOver(false);

    const files = event.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  const downloadImage = (imageUrl, filename) => {
    const link = document.createElement('a');
    link.href = imageUrl;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const resetImages = () => {
    if (originalImage) URL.revokeObjectURL(originalImage);
    if (grayscaleImage) URL.revokeObjectURL(grayscaleImage);
    setOriginalImage(null);
    setGrayscaleImage(null);
    setError(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="container">
      <h1 className="app-title">Image to Grayscale Converter</h1>

      <div className="upload-section">
        <div
          className={`upload-area ${isDragOver ? 'dragover' : ''}`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          onClick={handleUploadClick}
        >
          <div className="upload-icon">📁</div>
          <div className="upload-text">Click to upload or drag and drop</div>
          <div className="upload-hint">Supports: JPG, PNG, GIF, BMP</div>
        </div>

        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          onChange={handleFileSelect}
          className="file-input"
        />

        {error && <div className="error">{error}</div>}

        {isLoading && <div className="loading">Processing image...</div>}
      </div>

      {(originalImage || grayscaleImage) && (
        <div className="images-container">
          {originalImage && (
            <div className="image-card">
              <div className="image-title">Original Image</div>
              <img
                src={originalImage}
                alt="Original"
                className="image-display"
              />
              <button
                className="download-btn"
                onClick={() => downloadImage(originalImage, 'original_image.jpg')}
              >
                Download Original
              </button>
            </div>
          )}

          {grayscaleImage && (
            <div className="image-card">
              <div className="image-title">Grayscale Image</div>
              <img
                src={grayscaleImage}
                alt="Grayscale"
                className="image-display"
              />
              <button
                className="download-btn"
                onClick={() => downloadImage(grayscaleImage, 'grayscale_image.jpg')}
              >
                Download Grayscale
              </button>
            </div>
          )}
        </div>
      )}

      {(originalImage || grayscaleImage) && (
        <div style={{ textAlign: 'center', marginTop: '2rem' }}>
          <button
            className="download-btn"
            style={{ maxWidth: '200px' }}
            onClick={resetImages}
          >
            Upload New Image
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
